    if (!$currTbMatchedTh.hasClass('sorting') &&
        !$currTbMatchedTh.hasClass('sorting_asc') &&
        !$currTbMatchedTh.hasClass('sorting_desc')) return;

    // Simulate the click
    $currTbMatchedTh.trigger('click');

    // Grab the state frm <th> & Update the sorting state for <li>
